import React, {useEffect, useState} from "react";

const swiperSlide = {
    width: '100px',
    height: '500px',

    img: {
        display: 'block',
        width: '100%',
        height: '100%',
        objectFit: 'cover'
    }
}

function MainCardCarousel(props) {

    useEffect(() => {
        if (document.querySelector(
            'script[src="https://cdn.jsdelivr.net/npm/swiper@10/swiper-element-bundle.min.js"]'
        ))
            return;

        const script = document.createElement("Script");
        script.src = "https://cdn.jsdelivr.net/npm/swiper@10/swiper-element-bundle.min.js";
        script.async = true;
        document.body.appendChild(script);
    }, []);

    return (
        <div className={'my-3'}>
            <h1>{props.title}</h1>
            <swiper-container className="mySwiper" slides-per-view="4" space-between="30" navigation="true" loop="true" hiddenClass>
                <swiper-slide><img src="/image/1.png" style={swiperSlide.img} />책제목값 불러올 영역</swiper-slide>
                <swiper-slide><img src="/image/1.png" style={swiperSlide.img} />책제목값 불러올 영역</swiper-slide>
                <swiper-slide>Slide 3</swiper-slide>
                <swiper-slide>Slide 4</swiper-slide>
                <swiper-slide>Slide 5</swiper-slide>
                <swiper-slide>Slide 6</swiper-slide>
                <swiper-slide>Slide 7</swiper-slide>
                <swiper-slide>Slide 8</swiper-slide>
            </swiper-container>
        </div>
    )
}

export default MainCardCarousel;
