import React, {useState} from "react";

function BoardWrite(props) {

    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const onChangeTitle = (e) => {
        setTitle(e.target.value);
    }

    const onChangeContent = (e) => {
        setContent(e.target.value);
    }


    return (
        <div className={'container'}>
            <div className={'row'}>
                <div className={'col-sm-6 mx-auto'}>
                    <div>

                    </div>
                    <div>
                        <input type={'text'} className={'form-control'} value={title} placeholder={'글 제목을 입력하세요'}/>
                    </div>
                    <div className={'my-3'}>
                        <textarea className={'form-control'} rows={6} value={content} placeholder={'글 내용을 입력하세요'}></textarea>
                    </div>
                </div>
                <div className={'d-grid'}>
                    <button type={'button'} className={'btn'}>목록</button>
                    <button type={'button'} className={'btn'}>글등록</button>
                </div>
            </div>
        </div>
    )

}

export default BoardWrite;
